import { useState, useEffect } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000/api/v1';

export function useFeatureManagement() {
  const [features, setFeatures] = useState([]);
  const [sqlSets, setSqlSets] = useState([]);
  const [sqlStatements, setSqlStatements] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchData = async () => {
    setLoading(true);
    setError(null);
    try {
      const [featuresRes, sqlSetsRes, sqlStatementsRes] = await Promise.all([
        axios.get(`${API_BASE_URL}/features`),
        axios.get(`${API_BASE_URL}/sql/sets`),
        axios.get(`${API_BASE_URL}/sql/statements`)
      ]);

      setFeatures(featuresRes.data.items || []);
      setSqlSets(sqlSetsRes.data || []);
      setSqlStatements(sqlStatementsRes.data || []);
    } catch (err) {
      setError(err.response?.data?.detail || 'Failed to fetch data');
      console.error('Error fetching data:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const refreshData = () => {
    fetchData();
  };

  return {
    features,
    sqlSets,
    sqlStatements,
    loading,
    error,
    refreshData,
  };
}
